function navSelect(e){var t=$(e.srcElement);$(".nav-ul li a").removeClass("selected"),t.addClass("selected")}function clearSelected(){$(".nav-ul li a").removeClass("selected")}function mobileNavSelect(e){var t=$(e.srcElement);$(".sidenav a").removeClass("selected"),t.addClass("selected")}function hello(e){var t=$(e.srcElement.children);$("div").removeClass("show-me"),t.addClass("show-me")}function bye(e){$("div").removeClass("show-me")}function openNav(){var e=window.innerWidth;document.getElementById("mySidenav").style.width=e>=600?"200px":"100%"}function closeNav(){document.getElementById("mySidenav").style.width="0"}$(document).ready(function(){$(".front").on("click",function(e){e.preventDefault(),$(this).siblings(".back").css("transform","rotateY(0deg)"),$(this).css("transform","rotateY(180deg)")}),$(".cardClose").on("click",function(e){$(this).parent(".back").css("transform","rotateY(180deg)"),$(this).closest(".back").siblings(".front").css("transform","rotateY(0deg)")})});